//How to play the game: move WASD to control Player 1 (blue block) and arrow keys to control Player 2 (red block)
//Right-side timer bar decrements as the game progresses. When it hits 0, the game ends
//The player whose colour fills more of the game board wins
//KEY0 restarts the game. KEY1 pauses the game

//Definitions
#include <stdbool.h>
#include<stdlib.h>
#include <string.h>
#define TIMER_BASE 0xFF202000
#define PS2_BASE 0xFF200100
#define KEY_BASE 0xFF200050
const int SCREEN_HEIGHT = 240;
const int SCREEN_WIDTH = 320;
const int BLOCK_SIDELENGTH = 20;
const int MAX_TICKS = 240;

//Headers and global variables for the drawing functions
void clear_screen(int colour);
void plot_pixel(int x, int y, short int line_color);
void draw();
void draw_rectangle(int x, int width, int y, int height, int colour);
void swap(int *a, int *b);
int pixel_buffer_start;
short int Buffer1[240][512]; // 240 rows, 512 (320 + padding) columns
short int Buffer2[240][512];
volatile int * pixel_ctrl_ptr = (int *) 0xFF203020;
int blue = 0x2C3B; //rgb565 color picker: https://barth-dev.de/online/rgb565-color-picker/
int red = 0xF1C8;
int white = 0xFFFF;
void draw_countdown();
void draw_paused(bool on);
void draw_blocks();
void wait_for_vsync();
//For writing text
void write_char(int x, int y, char c);
volatile char *char_ptr = (char *) 0x09000000;
void write_message(int x, int y, char* c);

//Headers and global variables for the FSM determining which screen to show
//int grid_content[180] = {0}; //at grid length of 20, (16 - 1)x12 grid
int grid[15][12];
int grid_xsize = 15;
int grid_ysize = 12;
//int grid_size = 180; //sizeof(array) is annoying.
int game_ticks = 0; //number of timer ticks recorded. when this exceeds MAX_TICKS, end the game
void game_over();
void start_game();
int game_state = 0; //0 = start screen, 1 = game, 2 = paused screen, 3 = game over
int grid_ver = 1;
int game_count = 0;

//Headers and global variables that determine object directions
struct player{
	int x_coord;
	int y_coord;
	int dir_x;
	int dir_y;
	int dir;
	int col;
	int last_x;
	int last_y;
	int index_x;
	int index_y;
	bool powered;
	int power_time;
	int last_dir;
};
void increment(struct player *p);
struct player player1 = {0, 0, BLOCK_SIDELENGTH, 0, 1, 0x2C3B, 0, 0, 0, 0, false, 0, 1}; //start top left
struct player player2 = {SCREEN_WIDTH - 2*BLOCK_SIDELENGTH, SCREEN_HEIGHT - BLOCK_SIDELENGTH, BLOCK_SIDELENGTH*-1, 0, 3, 0xF1C8, 0, 0, 15, 12, false, 0, 3}; //start bottom right

//Headers and global variables for interrupts
static void handler(void) __attribute__ ((interrupt ("machine")));
//For PS2 keyboard
void set_PS2();
volatile int *PS2_ptr = (int*) 0xFF200100;
void keyboard_ISR();
//For interval timer
void set_itimer();
volatile int *timer_ptr = (int *) TIMER_BASE;
void itimer_ISR();
int clock_time = 100; //100MZ clock
//For keys
volatile int *KEY_ptr = (int *) KEY_BASE;
void set_KEYS();
void KEY_ISR();

