//************************************************** Object direction function **************************************************

//Increment is called every time interval timer is triggered
void increment(struct player *p){
	
	//Directions: -1 = don't change, 1 = right, 2 = down, 3 = left, 4 = up
	int direction = p->dir;
	int dx = p->dir_x;
	int dy = p->dir_y;
	int x = p->x_coord;
	int y = p->y_coord;
	
	//Add velocity to coordinates
		int new_x = x + dx;
		int new_y = y + dy;
	
	if(p -> powered){
		if(p -> power_time <= 0){
			p -> powered = false;
		}else{
			p -> power_time--;
		}
	}

	if(x % 20 == 0 && y % 20 == 0){
			if(direction == 1 ){ //right
				dx = 1;
				dy = 0;
			}else if(direction == 2){ //down
				dx = 0;
				dy = 1;
			}else if(direction == 3){ //left
				dx = -1;
				dy = 0;
			}else if(direction == 4){ //up
				dx = 0;
				dy = -1;
			}
		if(p -> powered){
			dx *=2;
			dy *=2;
		}
		new_x = x + dx;
		new_y = y + dy;
		//Check that coordinates are not out of bound, set velocity to 0 if they are
		if(new_x <= 0){
			new_x = 0;
			dx = 0;
		}else if(new_x >= SCREEN_WIDTH - 2*BLOCK_SIDELENGTH){
			new_x = SCREEN_WIDTH - 2*BLOCK_SIDELENGTH;
			dx = 0;
		}
		if(new_y <= 0){
			new_y = 0;
			dy = 0;
		}else if(new_y >= SCREEN_HEIGHT - BLOCK_SIDELENGTH){
			new_y = SCREEN_HEIGHT - BLOCK_SIDELENGTH;
			dy = 0;
		}

		//Check that the two boxes are not going to hit each other
		/*if(p->col == player1.col){
			if((new_x + BLOCK_SIDELENGTH > player2.x_coord && new_x <= player2.x_coord && new_y + BLOCK_SIDELENGTH > player2.y_coord && new_y < player2.y_coord && player1.dir == 1) ||
			   (new_x + BLOCK_SIDELENGTH >= player2.x_coord && new_x < player2.x_coord && new_y + BLOCK_SIDELENGTH > player2.y_coord && new_y < player2.y_coord && player1.dir == 3) ||
			   (new_x + BLOCK_SIDELENGTH > player2.x_coord && new_x < player2.x_coord && new_y + BLOCK_SIDELENGTH > player2.y_coord && new_y <= player2.y_coord && player1.dir == 4) ||
			   (new_x + BLOCK_SIDELENGTH > player2.x_coord && new_x < player2.x_coord && new_y + BLOCK_SIDELENGTH >= player2.y_coord && new_y < player2.y_coord && player1.dir == 2)){
				new_x = x;
				new_y = y;
				dx = 0;
				dy = 0;
			}

		}else{
			if((new_x + BLOCK_SIDELENGTH > player1.x_coord && new_x <= player1.x_coord && new_y + BLOCK_SIDELENGTH > player1.y_coord && new_y < player1.y_coord && player2.dir == 1) ||
			   (new_x + BLOCK_SIDELENGTH >= player1.x_coord && new_x < player1.x_coord && new_y + BLOCK_SIDELENGTH > player1.y_coord && new_y < player1.y_coord && player2.dir == 3) ||
			   (new_x + BLOCK_SIDELENGTH > player1.x_coord && new_x < player1.x_coord && new_y + BLOCK_SIDELENGTH >= player1.y_coord && new_y < player1.y_coord && player2.dir == 4) ||
			   (new_x + BLOCK_SIDELENGTH > player1.x_coord && new_x < player1.x_coord && new_y + BLOCK_SIDELENGTH > player1.y_coord && new_y <= player1.y_coord && player2.dir == 2)){
				new_x = x;
				new_y = y;
				dx = 0;
				dy = 0;
			}
		}*/

		//Check that the car is not going to hit a block
		if(direction == 1 ){ //right
				p -> index_x = (new_x + BLOCK_SIDELENGTH -1)/BLOCK_SIDELENGTH;
				p -> index_y = new_y/BLOCK_SIDELENGTH;
			}else if(direction == 2){ //down
				p -> index_x = new_x/BLOCK_SIDELENGTH;
				p -> index_y = (new_y + BLOCK_SIDELENGTH -1)/BLOCK_SIDELENGTH;
			}else if(direction == 3){ //left
				p -> index_x = (new_x)/BLOCK_SIDELENGTH;
				p -> index_y = new_y/BLOCK_SIDELENGTH;
			}else if(direction == 4){ //up
				p -> index_x = new_x/BLOCK_SIDELENGTH;
				p -> index_y = (new_y)/BLOCK_SIDELENGTH;
			}
		if(grid[p -> index_x][p -> index_y] == 1){
			new_x = x;
			new_y = y;
			dx = 0;
			dy = 0;
		} else if(grid[p -> index_x][p -> index_y] == 2){
			grid[p -> index_x][p -> index_y] = 0;
			p -> powered = true;
			p -> power_time = 240;
		}

		//Check that the two boxes are not going to hit each other
		if(player1.index_x == player2.index_x && player1.index_y == player2.index_y){
			new_x = x;
			new_y = y;
			dx = 0;
			dy = 0;
		}		
	}
	if(direction == 1 ){ //right
		p -> index_x = (new_x + BLOCK_SIDELENGTH -1)/BLOCK_SIDELENGTH;
		p -> index_y = new_y/BLOCK_SIDELENGTH;
	}else if(direction == 2){ //down
		p -> index_x = new_x/BLOCK_SIDELENGTH;
		p -> index_y = (new_y + BLOCK_SIDELENGTH -1)/BLOCK_SIDELENGTH;
	}else if(direction == 3){ //left
		p -> index_x = (new_x)/BLOCK_SIDELENGTH;
		p -> index_y = new_y/BLOCK_SIDELENGTH;
	}else if(direction == 4){ //up
		p -> index_x = new_x/BLOCK_SIDELENGTH;
		p -> index_y = (new_y)/BLOCK_SIDELENGTH;
	}
	//Apply new coordinates
	p->last_x = p->x_coord;
	p->last_y = p->y_coord;
	p->x_coord = new_x;
	p->y_coord = new_y;
	p->dir_x = dx;
	p->dir_y = dy;
	
	int grid_x = p->last_x/BLOCK_SIDELENGTH;
	int grid_y = p->last_y/BLOCK_SIDELENGTH;
	grid[grid_x][grid_y] = p->col;
	
	draw();
}

//************************************************** Drawing functions **************************************************

void clear_screen(int colour){
	//Draw the screen in one uniform colour
	int y, x;
	for (x = 0; x < SCREEN_WIDTH; x++)
		for (y = 0; y < SCREEN_HEIGHT; y++)
			plot_pixel(x, y, colour);
	
	//Remove all text on the screen
	for(int i = 0; i < 80; i++){
		for(int j = 0; j < 60; j++){
			write_char(i, j, 0x20);
		}
	}
}

void draw(){
	//Ignore function call if the game is not in the playing state (failsafe, this shouldn't happen)
	if(game_state != 1)
		return;
	
	//Colour the players' previous position in one colour
	draw_sprite(player1.last_x, BLOCK_SIDELENGTH, player1.last_y, BLOCK_SIDELENGTH, carb, player1.last_dir);
	draw_sprite(player2.last_x, BLOCK_SIDELENGTH, player2.last_y, BLOCK_SIDELENGTH, carb, player2.last_dir);
	
	//Draw the player sprites at their current position
	draw_sprite(player1.x_coord, BLOCK_SIDELENGTH, player1.y_coord, BLOCK_SIDELENGTH, car1, player1.dir);
	draw_sprite(player2.x_coord, BLOCK_SIDELENGTH, player2.y_coord, BLOCK_SIDELENGTH, car2, player2.dir);
	
	player1.last_dir = player1.dir;
	player2.last_dir = player2.dir;
	
	draw_path(&player1);
	draw_path(&player2);

	
	//Draw the timer bar at the right
	draw_countdown();
	
	wait_for_vsync(); // swap front and back buffers on VGA vertical sync
    pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer
}
void draw_path(struct player *p){
		
	if(p -> dir_x >= 1 ){ //right
		draw_rectangle(p -> last_x, p->dir_x, p -> last_y, BLOCK_SIDELENGTH, p->col);
	}else if(p -> dir_y >=  1){ //down
		draw_rectangle(p -> last_x, BLOCK_SIDELENGTH, p -> last_y, p->dir_y, p->col);
	}else if(p -> dir_x <=  -1){ //left
		draw_rectangle(p -> last_x + BLOCK_SIDELENGTH + p->dir_x, p->dir_x*-1, p -> last_y, BLOCK_SIDELENGTH, p->col);
	}else if(p -> dir_y <= -1){ //up
		draw_rectangle(p -> last_x, BLOCK_SIDELENGTH, p -> last_y + BLOCK_SIDELENGTH+ p->dir_y, p->dir_y*-1, p->col);
	}
}

void swap(int *a, int *b){
	int temp = *a;
	*a = *b;
	*b = temp;
}

//Draw any 20x20 sprite (car, block)
void draw_sprite(int x, int width, int y, int height, const struct RGB s[20][20], int dir){
	for(int i = 0; i < width; i++){
		for(int j = 0; j < height; j++){
			struct RGB c = s[i][j];
			if(c.r != -1){ //-1 is value set for a transparent pixel
				//Convert RGB into RGB565 integer
				unsigned short int red = (c.r * 32/256) & 0b11111;
				unsigned short int green = (c.g * 64/256) & 0b111111;
				unsigned short int blue = (c.b * 32/256) & 0b11111;
				int colour = (red << 11) + (green << 5) + blue;
				
				//Depending on the direction (for player car sprites), rotate the sprite. Standard orientation should call dir = 3
				switch(dir){
					case 1:
						plot_pixel(x + width - i - 1, y + j, colour);
						break;
					case 2:
						plot_pixel(x + j, y + height - i, colour);
						break;
					case 3:
						plot_pixel(x + i, y + j, colour);
						break;
					case 4:
						plot_pixel(x + j, y + i, colour);
						break;
					default:
						plot_pixel(x + width - i - 1, y + j, colour);
				}
			}
		}
	}
}
void draw_checkboard(struct player *p){
    bool flip = true;
    int colour;
    for(int x = p->last_x; x < BLOCK_SIDELENGTH; x++){
        for(int y = p->last_y; y < BLOCK_SIDELENGTH; y++){
            if(flip)
                colour = p->col;
            else{
                if(p->col == red)
                    colour = 0x5926;
                else
                    colour = 0x216A;
            }
            plot_pixel(x, y, colour);
            flip = !flip;
        }
        flip = !flip;
    }
}
void draw_rectangle(int x, int width, int y, int height, int colour){
	for(int i = 0; i < width; i++){
		for(int j = 0; j < height; j++){
			plot_pixel(x + i, y + j, colour);
		}
	}
}

void plot_pixel(int x, int y, short int line_color)
{
    volatile short int *one_pixel_address;
	one_pixel_address = (volatile short int *)(pixel_buffer_start + (y << 10) + (x << 1));
    *one_pixel_address = line_color;
}

void write_char(int x, int y, char c) {
  // VGA character buffer
  volatile char * character_buffer = char_ptr + (y<<7) + x;
  *character_buffer = c;
}

void write_message(int x, int y, char* c){
	while(*c){
		write_char(x, y, *c);
		x++;
		c++;
	}
}

//This draws the countdown bar in the right
void draw_countdown(){
	//White bar is drawn at start of game, this draws a black rectangle to reduce its size
	int height = SCREEN_HEIGHT / MAX_TICKS; //this is height of each tick
	draw_rectangle(SCREEN_WIDTH - BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, 0, height*game_ticks, 0);
	draw_rectangle(SCREEN_WIDTH - BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, height*game_ticks, SCREEN_HEIGHT-height*game_ticks, white);
	
	//Draw the timer border sprite
	for(int i = 0; i < SCREEN_HEIGHT; i++){
		for(int j = 0; j < 20; j++){
			struct RGB c = timer_border[i][j];
			if(c.r != -1){
				unsigned short int red = (c.r * 32/256) & 0b11111;
				unsigned short int green = (c.g * 64/256) & 0b111111;
				unsigned short int blue = (c.b * 32/256) & 0b11111;
				int colour = (red << 11) + (green << 5) + blue;
					plot_pixel(SCREEN_WIDTH - BLOCK_SIDELENGTH + j, i, colour);
			}
		}
	}
}

//This draws the paused symbol and erases it when unpaused
void draw_paused(bool on){
	int draw_col = 0;
	if(on)
		draw_col = white;
	int bar_height = BLOCK_SIDELENGTH * 6;
	int start_x = (SCREEN_WIDTH - 4 * BLOCK_SIDELENGTH)/2;
	int start_y = (SCREEN_HEIGHT - bar_height)/2;
	
	if(on){
		draw_rectangle(start_x, BLOCK_SIDELENGTH, start_y, bar_height, draw_col);
		draw_rectangle(start_x + 3 * BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, start_y, bar_height, draw_col);
	}else{
		//redraw the sprites in case the pause button covered them
		draw_blocks();
		draw_sprite(player1.x_coord, BLOCK_SIDELENGTH, player1.y_coord, BLOCK_SIDELENGTH, car1, player1.dir);
		draw_sprite(player2.x_coord, BLOCK_SIDELENGTH, player2.y_coord, BLOCK_SIDELENGTH, car2, player2.dir);
		
		//colour the grid the pause button covered with black/red/blue
		for(int i = 0; i < 6; i++){
			for(int j = 0; j < 2; j++){
				int grid_col = grid[(start_x + j * 3 * BLOCK_SIDELENGTH)/BLOCK_SIDELENGTH][(start_y + i * BLOCK_SIDELENGTH)/BLOCK_SIDELENGTH];
				if(grid_col != 1)
					draw_rectangle(start_x + j * 3 * BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, start_y + i * BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, grid_col);
			}
		}
	}
	
	wait_for_vsync(); // swap front and back buffers on VGA vertical sync
    pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer
}

void draw_blocks(){
	for(int i = 0; i < grid_xsize; i++){
		for(int j = 0; j < grid_ysize; j++){
			if(grid[i][j] == 1){
				draw_sprite(i*BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, j*BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, block, 3);
			}else if(grid[i][j] == 2){
				draw_sprite(i*BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, j*BLOCK_SIDELENGTH, BLOCK_SIDELENGTH, powerup, 4);
			}
		}
	}
}

//************************************************** Game over functions **************************************************
//Changes screen to the winning player's colour. White if they are tied
void game_over(){
	//Set velocities to 0
	player1.dir_x = 0;
	player1.dir_y = 0;
	player2.dir_x = 0;
	player2.dir_y = 0;
	
	int blue_score = 0;
	int red_score = 0;

	for(int i = 0; i < grid_xsize; i ++){
		for(int j = 0; j < grid_ysize; j++){
			if(grid[i][j] == blue)
				blue_score++;
			else if(grid[i][j] == red)
				red_score++;
		}
	}
	
	//Clear screen based on winning colour
	char* winner = "PLAYER 1 WINS";
	if(blue_score > red_score)
		clear_screen(blue);
	else if(red_score > blue_score){
		clear_screen(red);
		winner = "PLAYER 2 WINS";
	}else{
		clear_screen(0);
		winner = "PLAYERS TIED";
	}
	
	//Add text
	char* message = "GAME OVER";
	write_message(36, 25, message);
	write_message(34, 30, winner);
	
	wait_for_vsync(); // swap front and back buffers on VGA vertical sync
    pixel_buffer_start = *(pixel_ctrl_ptr + 1); // new back buffer
}

void wait_for_vsync()
{
	volatile int * pixel_ctrl_ptr = (int *) 0xff203020; // base address
	int status;
	*pixel_ctrl_ptr = 1; // start the synchronization process
	// - write 1 into front buffer address register
	status = *(pixel_ctrl_ptr + 3); // read the status register
	while ((status & 0x01) != 0) // polling loop waiting for S bit to go to 0
		{
		status = *(pixel_ctrl_ptr + 3);
	}
}
void set_BUFFER(){
	
	 /* set front pixel buffer to Buffer 1 */
    *(pixel_ctrl_ptr + 1) = (int) &Buffer1; // first store the address in the  back buffer
    /* now, swap the front/back buffers, to set the front buffer location */
    wait_for_vsync();
    /* initialize a pointer to the pixel buffer, used by drawing functions */
    pixel_buffer_start = *pixel_ctrl_ptr;
    clear_screen(0); // pixel_buffer_start points to the pixel buffer

    /* set back pixel buffer to Buffer 2 */
    *(pixel_ctrl_ptr + 1) = (int) &Buffer2;
    pixel_buffer_start = *(pixel_ctrl_ptr + 1); // we draw on the back buffer
    clear_screen(0); // pixel_buffer_start points to the pixel buffer
}